complete <- function(directory, id = 1:332) {		## Copyright George W. Frink III		## Written by George W. Frink III 		## Contact: frink@southernconnections.net	        ## 'directory' is a character vector of length 1 indicating	        ## the location of the CSV files	        ## 'id' is an integer vector indicating the monitor ID numbers	        ## to be used	        ## Return a data frame of the form:	        ## id nobs	        ## 1  117	        ## 2  1041	        ## 	        ## where 'id' is the monitor ID number and 'nobs' is the	        ## number of complete cases		library(sqldf)		n <- 1		fileName <- paste( directory, "/", sprintf("%03d", id[1]),".csv", sep = "" )		tempTbl <- read.csv(fileName,  sep = ",", quote = "\"",header = TRUE, dec = ".", fill = TRUE, comment.char = "#")		bigTbl <- tempTbl[complete.cases(tempTbl),]		completeCasesCount <- length(bigTbl)		len <- length(id)		i <- 2        nv <- c("   ",n)		colheadings <- c("##   ", "id ", " nobs " )		cCompleteCasesVec <- c(nv, id[i], completeCasesCount )		names(cCompleteCasesVec) <- colheadings		if(len >= 2 ) {			for( i in 2:len ) { 				n <- n + 1								fileName <- paste( directory, "/", sprintf("%03d", id[i]),".csv", sep = "" ) 				tempTbl <- read.csv(fileName,  sep = ",", quote = "\"",header = TRUE, dec = ".", fill = TRUE, comment.char = "#")				completeCasesTable <- tempTbl[complete.cases(tempTbl),]				completeCasesCount <- sum(complete.cases(tempTbl))                nv <- c("   ",n)				completeCasesVec <- c( nv, id[i], completeCasesCount )				cCompleteCasesVec <- rbind(cCompleteCasesVec, completeCasesVec,deparse.level = 0)				}			}			 cCompleteCasesVec					}